import React, { useState } from 'react';
import { Card, Form, Button, Badge } from 'react-bootstrap';
import { fileService, blogService } from '../services/api'; // Assuming blogService is where createBlogPost lives

function BlogEditor({ onPostCreated }) {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [category, setCategory] = useState('');
  const [tags, setTags] = useState('');
  const [mediaUrls, setMediaUrls] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [uploading, setUploading] = useState(false);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setUploading(true);
    try {
      const response = await fileService.uploadFile(file);
      setMediaUrls([...mediaUrls, response.fileUrl]);
    } catch (error) {
      console.error('Failed to upload file:', error);
      alert('Failed to upload file. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  const removeMedia = (indexToRemove) => {
    setMediaUrls(mediaUrls.filter((_, index) => index !== indexToRemove));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title.trim() || !content.trim()) return;

    setIsSubmitting(true); // --- Show the animation ---
    try {
      const postData = {
        title,
        content,
        category: category || null,
        tags: tags ? tags.split(',').map(tag => tag.trim()).filter(tag => tag) : [],
        mediaUrls: mediaUrls.length > 0 ? mediaUrls : null
      };

      // Call the API to create the post
      await blogService.createBlogPost(postData);

      // Reset form after successful submission
      setTitle('');
      setContent('');
      setCategory('');
      setTags('');
      setMediaUrls([]);

      // Notify parent component
      if (onPostCreated) {
        onPostCreated();
      }

    } catch (error) {
      console.error('Failed to publish post:', error);
      alert('Failed to publish post. Please try again.');
    } finally {
      setIsSubmitting(false); // --- Hide the animation ---
    }
  };

  return (
    <> 
      {/* === ADDED LOADING OVERLAY RENDER === */}
      {isSubmitting && (
        <div className="loading-overlay">
          <div className="spinner"></div>
        </div>
      )}

      <Card>
        <Card.Header as="h5">Create New Blog Post</Card.Header>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter post title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Content</Form.Label>
              <Form.Control
                as="textarea"
                rows={10}
                placeholder="Write your blog content here..."
                value={content}
                onChange={(e) => setContent(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Category (optional)</Form.Label>
              <Form.Control
                type="text"
                placeholder="e.g., Technology, Lifestyle"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Tags (optional, comma-separated)</Form.Label>
              <Form.Control
                type="text"
                placeholder="e.g., react, javascript, webdev"
                value={tags}
                onChange={(e) => setTags(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Upload Media (optional)</Form.Label>
              <Form.Control
                type="file"
                onChange={handleFileUpload}
                disabled={uploading}
                accept="image/*"
              />
              {uploading && <small className="text-muted">Uploading...</small>}
            </Form.Group>

            {mediaUrls.length > 0 && (
              <div className="mb-3">
                <h6>Attachments:</h6>
                <div className="d-flex flex-wrap gap-2">
                  {mediaUrls.map((url, index) => (
                    <Badge key={index} bg="secondary" className="d-flex align-items-center">
                      Media {index + 1}
                      <button
                        type="button"
                        className="btn-close btn-close-white ms-2"
                        style={{ fontSize: '0.6rem' }}
                        onClick={() => removeMedia(index)}
                      ></button>
                    </Badge>
                  ))}
                </div>
              </div>
            )}

            <div className="d-flex justify-content-end">
              <Button
                variant="primary"
                type="submit"
                disabled={isSubmitting || !title.trim() || !content.trim()}
              >
                {isSubmitting ? 'Publishing...' : 'Publish Post'}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </>
  );
}

export default BlogEditor;
